import React, {useState} from 'react';
import AppLayout from "../components/AppLayout";
import Head from "next/head";
import {Form, Input, Checkbox,Button} from 'antd';
import {red} from "@ant-design/colors";

const Signup = () => {

    // const [id,setId] = useState('');
    // const [nick,setNick] = useState('');
    // const [password,setPassword] = useState('');
    const [passwordCheck,setPasswordCheck] = useState('');
    const [term,setTerm] = useState(false);
    const[passwordError,setPasswordError] = useState(false);
    const [termError, setTermError] = useState(false);




    const varData = {"id":id,"nick":nick,"password":password,"term":term,"passwordCheck":passwordCheck};

    const onFinish = () => {
        if (password !== passwordCheck){
            return setPasswordError(true);
        }

        if (!term){
            return setTermError(true);
        }
        console.log(
            varData
        );
    };
    const onChangeId = (e) => {
        setId(e.target.value);
    };
    const onChangeNick = (e) => {
        setNick(e.target.value);
    };
    const onChangePassword = (e) => {
        setPassword(e.target.value);
    };
    const onChangePasswordChk = (e) => {
        setPasswordError(e.target.value !== password);
        setPasswordCheck(e.target.value);
    };
    const onChangeTerm = (e) => {
        setTermError(false);
        setTerm(e.target.checked);
    };

    const useInput = (initValue = null) => {
        const [value,setter] = useState(initValue);
        const handler = (e) => {
            setter(e.target.value);
        };
        return [value,handler];
    };

    const [id,setId] = useInput('');
    const [nick,setNick] = useInput('');
    const [password,setPassword] = useInput('');

    return <>
        <Head>
            <title>SNS</title>
            <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/antd/3.18.1/antd.css"/>
        </Head>
        <AppLayout>
            <Form onFinish= {onFinish} style = {{padding:10}}>
                <div>
                    <label htmlFor="user-id">아이디</label>
                    <br/>
                    <Input name = "user-id" value = {id} required onChange = {onChangeId}/>
                </div>
                <div>
                    <label htmlFor="user-nick">닉네임</label>
                    <br/>
                    <Input name = "user-nick" value = {nick} required onChange = {onChangeNick}/>
                </div>
                <div>
                    <label htmlFor="user-pass">비밀번호</label>
                    <br/>
                    <Input name = "user-pass" type = "password" value = {password} required onChange = {onChangePassword}/>
                </div>

                <div>
                        <label htmlFor="user-pass-chk">비밀번호 확인</label>
                    <br/>
                    <Input name = "user-pass-chk" type = "password" value = {passwordCheck} required onChange = {onChangePasswordChk}/>
                    {passwordError && <div style = {{color:'red'}}>비밀번호가 같아야 합니다.</div>}

                </div>

                <div>
                    <Checkbox name="user-term" defaultChecked={term} onChange={onChangeTerm}>
                        약관에 동의합니다.
                    </Checkbox>
                    {termError && <div style = {{color:'red'}}>약관에 동의 하셔야 합니다.</div>}
                </div>

                <div style={{marginTop:10}}>
                    <Button type = "primary" htmlType = "submit">가입하기</Button>
                </div>

            </Form>
        </AppLayout>
    </>
};

export default Signup;
